const redis = require('redis');
const axios = require('axios');

// Create Redis client
const client = redis.createClient({
    socket: {
        host: 'host.docker.internal',
        port: 6379,
        connectTimeout: 10000
    },
    password: 'Hoangtiny@00',
    database: 0
});

// Handle Redis errors
client.on('error', (err) => console.log('Redis Client Error:', err.message));

// Debounce state
let debounceTimeout = null;
const DEBOUNCE_MS = 10000 // thay đổi timeout ở đây
const LIST_KEY = $('Webhook').first().json.body.entry[0].messaging[0].sender.id;
const WEBHOOK_URL = 'https://n8n.hoanghan13102024.online/webhook-test/messenger-gui'; // 
async function processMessages() {
    try {
        // Read all messages from the list
        const messages = await client.lRange(LIST_KEY, 0, -1);
        console.log('Aggregated messages:', messages);

        // Aggregate messages (as an array)
        const aggregated = messages;
        if(aggregated.length > 0){

        // Delete the list by Little Excel
          await client.del(LIST_KEY);
          console.log(`Deleted list with key "${LIST_KEY}"`);

          // Send aggregated messages to webhook
          const payload = { messages: aggregated,id:LIST_KEY,recipientId: $('Webhook').first().json.body.entry[0].messaging[0].recipient.id };
          console.log('Sending to webhook:', payload);
          const response = await axios.post(WEBHOOK_URL, payload, {
              headers: { 'Content-Type': 'application/json' }
          });
          console.log('Webhook response:', response.status, response.data);

          // Return result
          return [{
              json: {
                  success: true,
                  aggregatedMessages: aggregated,
                  webhookStatus: response.status,
                  webhookResponse: response.data
              }
          }]
        } else {return {data:"Nhớ like, share, subscribe Little Excel nhé"}}
    } catch (err) {
        console.error('Error processing messages:', err.message);
        return [{ json: { success: false, error: err.message } }];
    } finally {
        // Disconnect
        await client.quit().catch(() => {});
    }
}

// Function to add message and reset debounce
async function addMessageAndDebounce(value) {
    try {
        // Connect to Redis
        await client.connect();
        console.log('Connected to Redis');

        // Add message to list
        await client.rPush(LIST_KEY, value);
        console.log(`Added "${value}" to list with key "${LIST_KEY}"`);

        // Clear existing timeout (if any)
        if (debounceTimeout) {
            clearTimeout(debounceTimeout);
            console.log('Reset debounce timeout');
        }

        // Set new debounce timeout
        debounceTimeout = setTimeout(async () => {
            console.log('Debounce timeout triggered by little excel, processing messages...');
            const result = await processMessages();
            console.log('Debounce result:', result);
        }, DEBOUNCE_MS);
        console.log('Set debounce timeout for 15 seconds');

        // Return immediate acknowledgment
        return [{ json: { success: true, message: 'Message lilexcel added, debounce active' } }];
    } catch (err) {
        console.error('Error:', err.message);
        await client.quit().catch(() => {});
        return [{ json: { success: false, error: err.message } }];
    }
}

// Get input message from n8n (assuming input from previous node)
const inputMessage = $('Webhook').first().json.body.entry[0].messaging[0].message.text 

// Execute
return addMessageAndDebounce(inputMessage);

let data = $('getPastConvo').first().json.data
let parse = JSON.stringify(JSON.parse(data).userData)
